  `contents_videos` text COMMENT '视频地址的集合--多个图片用,分开',
  `contents_downs` text COMMENT '下载地址的集合--多个图片用,分开',
  `contents_hits` int(10) unsigned NOT NULL default '0' COMMENT '点击率',
  `contents_connect` varchar(255) NOT NULL default '' COMMENT '联系方式',
  `contents_recive` int(10) unsigned NOT NULL default '0' COMMENT '回复总次数',
  `contents_url` varchar(45) default NULL COMMENT '生成静态的地址',
  `contents_resource` varchar(100) default NULL COMMENT '来源',
  `contents_adduserid` int(10) unsigned NOT NULL default '0' COMMENT '添加者---用户表的主键',
  `contents_addtime` varchar(50) NOT NULL default '' COMMENT '添加时间',
  `contents_edituserid` varchar(45) NOT NULL default '' COMMENT '最后修改者',
  `contents_edittime` varchar(50) NOT NULL default '' COMMENT '修改时间',
  `contents_sort` int(10) unsigned NOT NULL default '1' COMMENT '排序--值为1-9',
  `contents_status` int(10) unsigned NOT NULL default '1' COMMENT '状态--1 激活 2 关闭',
  `contents_remark1` varchar(45) default NULL COMMENT '备用字段',
  `contents_remark2` varchar(45) default NULL COMMENT '备用字段',
  `contents_remark3` varchar(45) default NULL COMMENT '备用字段',
  PRIMARY KEY  (`contents_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='内容表--新闻、产品、信息、视频、图片、fals';

