--
-- Table structure for table `leetobest`.`credits`
--

DROP TABLE IF EXISTS `credits`;
CREATE TABLE `credits` (
  `credits_id` int(10) unsigned NOT NULL auto_increment,
  `channels_id` int(10) unsigned NOT NULL default '0' COMMENT '频道表主键',
